import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import BookCard from "../components/BookCard";

export default function PaginaPrincipal() {
  const [libros, setLibros] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("https://localhost:5072/api/libros") // Cambiá al URL de tu backend
      .then(res => res.json())
      .then(data => {
        setLibros(data);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, []);

  if (loading) return <p style={{ color: "white", textAlign: "center" }}>Cargando libros...</p>;

  return (
    <main>
      <div className="grid">
        {libros.map(libro => (
          <Link
            to={`/libro/${libro.id}`}
            key={libro.id}
            className="book"
            title={`Ver detalles de ${libro.titulo}`}
          >
            <img src={libro.urlPortada} alt={libro.titulo} />
            <div className="book-info">
              <div className="book-title">{libro.titulo}</div>
              <div className="book-author">{libro.autor}</div>
            </div>
          </Link>
        ))}
      </div>
    </main>
  );
}
