import React, { useEffect, useState } from "react";
import BookCard from "../components/BookCard"; // IMPORT CORRECTO
import "../Styles/Pa";      // IMPORT CSS

export default function PaginaPrincipal() {
  const [libros, setLibros] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("https://localhost:7070/api/libros") // Ajustar puerto del backend
      .then(res => res.json())
      .then(data => {
        setLibros(data);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, []);

  if (loading) return <p style={{ color: "white", textAlign: "center" }}>Cargando libros...</p>;

  return (
    <main>
      <div className="grid">
        {libros.map(libro => (
          <BookCard
            key={libro.id}
            title={libro.titulo}
            author={libro.autor}
            cover={libro.urlPortada}
          />
        ))}
      </div>
    </main>
  );
}
